import sys

good_genes = []
with open(sys.argv[1],'r') as genelist:
    for l in genelist:
        l = l.strip()
        good_genes.append(l)

with open(sys.argv[2],'r') as tfbs:
    for l in tfbs:
        l = l.strip().split('\t')
        gname = l[4]
        if gname in good_genes:
            print(*l,sep='\t')
 

